import {BASE_API} from "../utils/constants"

export async function getStudentsApi(){
    try {
        const url = `${BASE_API}/api/students/`;
        const response = await fetch(url);
        const result = response.json();
        
        return result;
    } catch (error) {
        throw error;
    }    
}

export async function addStudentApi(data, token){
    try {

        const formData = new FormData();
        formData.append('name', data.name);
        formData.append('partenal', data.paternal);
        formData.append('maternal', data.maternal);
        formData.append('grade', data.grade);
        formData.append('attorney', data.nameApoderado);
        formData.append('commune', data.name);
        formData.append('address_street', data.name);
        formData.append('address_number', data.name);
        formData.append('address_depart', data.name);
        formData.append('commune', data.name);
        
        const url = `${BASE_API}api/students/`;
        params={
            method: 'POST',
            headers:{
                Authorization: `Bearer ${token}`
            }
        }
    } catch (error) {
        throw error;
    }
}
