import React, {useState, useEffect} from 'react';
import "./AddEditGradeForm.scss";
import { Form, Button, Dropdown } from 'semantic-ui-react';
import { LEVEL, LETTER, DAY_TRIP } from "../../../../utils/constants";
import { useGrade } from '../../../../hooks';
import { useFormik } from 'formik';
import * as Yup from "yup";

export function AddEditGradeForm(props) {
  const { onClose } = props;
  const {addGrade} = useGrade();

  const formik = useFormik({
    initialValues: initialValues(),
    validationSchema: Yup.object(newSchema()),
    validateOnChange: false,
    onSubmit: (formValue) =>{
      console.log("Formulario enviado");
      console.log(formValue);
    }
  })
  return (
    
    <Form className='add-edit-grade-form' onSubmit={formik.handleSubmit}>
      <Dropdown 
      placeholder='Nivel' fluid selection search options={LEVEL} />
      <Dropdown placeholder='Letra' fluid selection search options={LETTER} />
      <Dropdown placeholder='Jornada' fluid selection search options={DAY_TRIP}/>
      <Button type='submit' primary content="Crear" onClick={()=>addGrade()}/>
    </Form>
  );
}

function initialValues(){
  return{
    level: "",
    letter: "",
    day_trip: "",
  }
}

function newSchema(){
  return{
    level: Yup.string().required(true),
    letter: Yup.string().required(true),
    day_trip: Yup.string().required(true),
  }
}
