import React, { useEffect, useState } from 'react';
import { TableStudentAdmin } from '../../Student/TableStudentAdmin';
import { useStudent } from '../../../../hooks';
import { Button, Dropdown } from 'semantic-ui-react';
import { RECORRIDO, IDA_VUELTA} from "../../../../utils/constants";
import { useGrade } from '../../../../hooks';
import { map } from 'lodash';
import "./"

export function AddAttendanceForm() {
  
  const { students, getStudents } = useStudent();
  const { getGrades, grades } = useGrade();
  const [gradesFormat, setGradesFormat] = useState([]);
  useEffect(() => {
    // eslint-disable-next-line react-hooks/exhaustive-deps
        getStudents()
    }, []);

    useEffect(() =>{ 
      // eslint-disable-next-line react-hooks/exhaustive-deps
      getGrades()
      
    }, []);
  
  useEffect(() =>{
      setGradesFormat(formatDropdownDataGrade(grades));
    }, [grades]);

    var rec = null
    var iv = null
    var curso = null
    function cambioRec(e){
      console.log(e.target.outerText);
      rec = e.target.outerText;
      console.log("ha sido cambiado a: ", rec);
    }
  
    function cambioIV(e){
      console.log(e.target.outerText);
      rec = e.target.outerText;
      console.log("ha sido cambiado a: ", iv);
    }
    function cambioCurso(e){
      console.log(e.target.outerText);
      rec = e.target.outerText;
      console.log("ha sido cambiado a: ", curso);
    }
    
  return (
    <>
      <div className='drop-attendance'>
        <Dropdown 
          placeholder='RECORRIDO'
          selection 
          search
          options={RECORRIDO}
          onChange={cambioRec}
        ></Dropdown>
        <Dropdown 
          placeholder='IDA/VUELTA'
          selection 
          search 
          options={IDA_VUELTA}
          onChange={cambioIV}
        ></Dropdown>
        <Dropdown
          placeholder='CURSO' 
          selection 
          search 
          options={gradesFormat} 
          onChange={cambioCurso}
        ></Dropdown>
      </div>
      <TableStudentAdmin students={students} />
      <Button  content="Enviar" positive primary fluid  />
    </>
  )
}

function formatDropdownDataGrade(data){
  return map(data, (item) =>({
    key: item.id,
    text: item.level + item.letter,
    value: item.id,
  }));
}
