import React, { useEffect, useState } from 'react';
import { TableStudentAdmin } from '../../Student/TableStudentAdmin';
import { useStudent } from '../../../../hooks';
import { Button, Dropdown } from 'semantic-ui-react';
import { RECORRIDO, IDA_VUELTA, SUBLEVEL} from "../../../../utils/constants";
import { useGrade, useAttendance } from '../../../../hooks';
import { map } from 'lodash';
import "./AddAtendance.scss"

export function AddAttendanceForm() {

  const { getAttendance, attendances } = useAttendance();
  const { students, getStudents } = useStudent();
  const { getGrades, grades } = useGrade();
  const [rec, setRec] = useState("");
  const [iv, setIv] = useState("");
  const [refetch, setRefetch] = useState(false);

  const onRefetch = () => setRefetch((prev) => !prev);

  function cambioRec(e){
    setRec(e.target.outerText);
    onRefetch()
  }
  function cambioIV(e){
    setIv(e.target.outerText);
    onRefetch();
  }
  useEffect(() =>{
    // eslint-disable-next-line react-hooks/exhaustive-deps
    getAttendance();
  }, [refetch]);

  useEffect(() =>{ 
    // eslint-disable-next-line react-hooks/exhaustive-deps
    getGrades()
  }, []);
  useEffect(() => {
    // eslint-disable-next-line react-hooks/exhaustive-deps
    getStudents()}, [refetch]);

  return (
    <>
      <div className='drop-attendance'>
        <Dropdown 
          placeholder='RECORRIDO'
          selection 
          search
          options={RECORRIDO}
          onChange={cambioRec}
        ></Dropdown>
        <Dropdown 
          placeholder='IDA/VUELTA'
          selection 
          search 
          options={IDA_VUELTA}
          onChange={cambioIV}
        ></Dropdown>
      </div>
      <TableStudentAdmin students={students} />
      <Button  content="Enviar" positive primary fluid  />
    </>
  )
}
