import React from 'react';
import "./TableGrade.scss";
import { Table, Button, Icon } from 'semantic-ui-react';
import { map } from 'lodash';
import "./TableGrade.scss";

export function TableGrade(props) {
  const { grades } = props;
  console.log(grades);
  return (
    <Table className="table-grade">
      <Table.Header>
        <Table.Row>
          <Table.HeaderCell>Nivel</Table.HeaderCell>
          <Table.HeaderCell>Letra</Table.HeaderCell>
          <Table.HeaderCell>Jornada</Table.HeaderCell>
          <Table.HeaderCell></Table.HeaderCell>
        </Table.Row>
      </Table.Header>

      <Table.Body>
        {map(grades, (grade, index) =>(
          <Table.Row key={index}>
            <Table.Cell>{grade.level}</Table.Cell>
            <Table.Cell>{grade.letter}</Table.Cell>
            <Table.Cell>{grade.day_trip}</Table.Cell>
            <Actions grades={grades} />
          </Table.Row>
        ))}
      </Table.Body>
    </Table>
  )
}

function Actions(props){
  const { grades } = props;
  return(
    <Table.Cell textAlign="right">
      <Button icon color='blule' onClick={() => console.log("Actualizar Curso")}>
        <Icon name='pencil' />
      </Button>
      <Button negative icon onClick={() => console.log("Eliminar Curso")}>
        <Icon name='trash' />
      </Button>
    </Table.Cell>
  )
}
