import React, { useEffect, useState } from 'react'
import { HeaderPage, TableStudentAdmin } from "../../components/Admin";
import { useStudent } from '../../hooks/useStudent';
import { Loader } from 'semantic-ui-react';
import { ModalBasic } from "../../components/Common";

export function StudentAdmin() {
  const [showModal, setShowModal] = useState(false);
  const [titleModal, setTitleModal] = useState(null);
  const [contentModal, setContentModal] = useState(null);

  const { loading, students, getStudents} = useStudent();

  useEffect(() => getStudents(), []);

  const openCloseModal = () => setShowModal((prev) => !prev);

  const addProduct = () =>{
    setTitleModal("Nuevo Estudiante");
    setContentModal()
  }
  
  return (
    <>
        <HeaderPage title="Alumnos" btnTitle="Nuevo alumno" />
        {loading ? (
          <Loader active inline="centered">
            Cargando ...
          </Loader>
        ):(
          <TableStudentAdmin students={students} />
        )}

        <ModalBasic show={showModal} onClose={openCloseModal} title={titleModal} children={contentModal} />
    </>
  )
}
