import { useState } from "react";
import { getStudentsApi, addStudentApi, updateStudentApi } from "../api/student";
import { useAuth } from "./useAuth";

export function useStudent(){
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);
    const [students, setStudents] = useState(null);
    const { auth } = useAuth();

    const getStudents = async () =>{
        try {
            setLoading(true);
            const response = await getStudentsApi();
            setLoading(false);
            setStudents(response);
        } catch (error) {
            setLoading(false);
            setError(error);
        }
    };

    const addStudent = async (data) =>{
        try {
            setLoading(true);
            await addStudentApi(data, auth.token);
            setLoading(false);
        } catch (error) {
            setLoading(false);
            setError(error);
        }
    }
    const updateStudent = async (data) =>{
        try {
            setLoading(true);
            await updateStudentApi(data, auth.token);
            setLoading(false);
        } catch (error) {
            setLoading(false);
            setError(error);
        }
    }
    return{
        loading,
        error,
        students,
        getStudents,
        addStudent,
    }
}
