import React, {useEffect, useState} from 'react';
import { Button, Dropdown } from 'semantic-ui-react';
import "./HeaderPage.scss";
import {  useGrade } from "../../../hooks";
import { map } from "lodash";


export function HeaderPage(props) {
    const [gradesFormat, setGradesFormat] = useState([]);

    const {title, btnTitle, btnClick, btnTitleTwo, btnClickTwo} = props;
    const { getGrades } = useGrade();
  return (
      <div className='header-page-admin'>
        <h2>{title}</h2>
        <div>
            {btnTitle && (
                <Button positive onClick={btnClick}>
                    {btnTitle}
                </Button>
            )}
            {btnTitleTwo && (
                <Button negative onClick={btnClickTwo}>
                    {btnClickTwo}
                </Button>
            )}
            <Dropdown placeholder='curso' fluid selection ></Dropdown>
        </div>
      </div>
  );
}

function formatDropdownData(data){
    return map(data, (item) =>({
      key: item.id,
      text: item.level + item.letter,
      value: item.id,
    }));
  }
