import React, { useState, useEffect} from 'react';
import { HeaderPage, TableGrade,AddEditGradeForm } from "../../components/Admin";
import { useGrade } from '../../hooks';
import { Loader } from 'semantic-ui-react';
import { ModalBasic } from "../../components/Common";

export function GradesAdmin() {
  const [showModal, setShowModal] = useState(false);
  const [titleModal, setTitleModal] = useState(null);
  const [contentModal, setContentModal] = useState(null);
  const [refetch, setRefetch] = useState(false);
  const { loading, grades, getGrades } = useGrade();

  useEffect(() => getGrades(), [refetch]);

  const openCloseModal = () => setShowModal((prev) => !prev);
  const onRefetch = () => setR((prev) => !prev);

  const addGrade = () =>{
    setTitleModal("Nuevo Curso");
    setContentModal(<AddEditGradeForm onClose={openCloseModal} />);
    openCloseModal();
  };
  
  return (
    <>
      <HeaderPage title="Cursos" btnTitle="Nuevo curso" btnClick={addGrade}/>
      {loading ? (
        <Loader active inline="centered">
          Cargando ...
        </Loader>
      ) : (
        <TableGrade grades={grades} />
      )}
      <ModalBasic 
        show={showModal}
        onClose={openCloseModal}
        title={titleModal}
        children={contentModal}
      />
    </>
  );
}
