import React, {useEffect, useState} from 'react';
import { Button, Dropdown } from 'semantic-ui-react';
import "./HeaderPage.scss";
import {  useGrade } from "../../../hooks";
import { map } from "lodash";
import { IDA_VUELTA, RECORRIDO } from "../../../utils/constants";



export function HeaderPage(props) {
    const [gradesFormat, setGradesFormat] = useState([]);

    const {title, btnTitle, btnClick, btnTitleTwo, btnClickTwo} = props;
    const { getGrades, grades } = useGrade();

    useEffect(() =>{ 
        // eslint-disable-next-line react-hooks/exhaustive-deps
        getGrades()}, []);

    useEffect(() =>{
        setGradesFormat(formatDropdownDataGrade(grades));
      }, [grades]);
  return (
      <div className='header-page-admin'>
        <h2>{title}</h2>
        <div>
            {btnTitle && (
                <Button positive onClick={btnClick}>
                    {btnTitle}
                </Button>
            )}
            {btnTitleTwo && (
                <Button negative onClick={btnClickTwo}>
                    {btnClickTwo}
                </Button>
            )}
            <Dropdown 
                placeholder='Curso'
                fluid
                selection 
                search 
                options={gradesFormat} 
            ></Dropdown>
            <Dropdown 
                placeholder='Jornada'
                fluid
                selection 
                search 
                options={IDA_VUELTA}
            ></Dropdown>
            <Dropdown 
                placeholder='Jornada'
                fluid
                selection 
                search 
                options={RECORRIDO}
            ></Dropdown>
        </div>
        
      </div>
  );
}

function formatDropdownDataGrade(data){
    return map(data, (item) =>({
      key: item.id,
      text: item.level + item.letter,
      value: item.id,
    }));
  }
