import { map } from 'lodash'
import React, {useEffect, useState} from 'react';
import { Table, Dropdown } from 'semantic-ui-react';
import moment from "moment";
import "moment/locale/es";
import { IDA_VUELTA, RECORRIDO } from "../../../../utils/constants";
import { useGrade, useAttendance } from '../../../../hooks';

import "./TableAttendance.scss"


export function TableAttendance(props) {
  //const {attendances} = props;
  const { getGrades, grades } = useGrade();
  const { getAttendance, attendances, getAttendanceByFilter } = useAttendance();
  const [refetch, setRefetch] = useState(false);
  const [gradesFormat, setGradesFormat] = useState([]);
  const [rec, setRec] = useState("");
  const [first, setfirst] = useState(second)
  const onRefetch = () => setRefetch((prev) => !prev);

  
  var iv = ""
  var curso = ""


  function cambioRec(e){
    console.log(e.target.outerText);
    setRec(e.target.outerText);
    console.log("el recorrido ha sido cambiado a: ", rec);
    onRefetch()
  }

  function cambioIV(e){
    console.log(e.target.outerText);
    iv = e.target.outerText;
    console.log("ha sido cambiado a: ", iv);
  }
  function cambioCurso(e){
    console.log(e.target.outerText);
    curso = e.target.outerText;
    console.log("ha sido cambiado a: ", curso);
  }
  useEffect(() =>{
    console.log("ruta ",rec);
    // eslint-disable-next-line react-hooks/exhaustive-deps
    getAttendanceByFilter("",rec,"","");
    console.log("se recargó la asistencia");
    console.log(attendances);
  }, [refetch]);

  useEffect(() =>{ 
    // eslint-disable-next-line react-hooks/exhaustive-deps
    getGrades()
  }, []);

useEffect(() =>{
    setGradesFormat(formatDropdownDataGrade(grades));
  }, [grades]);

useEffect(() => {
  
}, [])

  
  return (
    <>
      <div className='table-attendance'>
        <Dropdown 
          placeholder='RECORRIDO'
          selection 
          search
          options={RECORRIDO}
          onChange={cambioRec}
        ></Dropdown>
        <Dropdown 
          placeholder='IDA/VUELTA'
          selection 
          search 
          options={IDA_VUELTA}
          onChange={cambioIV}
        ></Dropdown>
        <Dropdown
          placeholder='CURSO' 
          selection 
          search 
          options={gradesFormat} 
          onChange={cambioCurso}
        ></Dropdown>
      </div>
      <Table>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Fecha</Table.HeaderCell>
            <Table.HeaderCell>Recorrido</Table.HeaderCell>
            <Table.HeaderCell>Ida/Vuelta</Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {map(attendances, (attendance, index)=>(
            <Table.Row key={index}>
              <Table.Cell>{moment(attendance.date).format("DD/MM/YYYY")}</Table.Cell>
              <Table.Cell>{attendance.idRoute}</Table.Cell>
              <Table.Cell>{attendance.roundTrip}</Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
      </Table>
    </>
  )
}

function formatDropdownDataGrade(data){
  return map(data, (item) =>({
    key: item.id,
    text: item.level + item.letter,
    value: item.id,
  }));
}
