import React, { useEffect, useState } from 'react';
import { Form, Button, Dropdown } from 'semantic-ui-react';
import { useGrade } from "../../../../hooks"
import "./AddEditStudentForm.scss";
import { map } from "lodash";
import { useFormik } from 'formik';
import * as Yup from "yup";

import { COMMUNE } from "../../../../utils/constants"
export function AddEditStudentForm() {
  const [gradesFormat, setGradesFormat] = useState([]);

  const { getGrades, grades } = useGrade();
  
  useEffect(() => getGrades(), [])
  useEffect(() =>{
    setGradesFormat(formatDropdownData(grades));
  }, [grades]);
  
  const formik = useFormik({
    initialValues: initialValues(),
    validationSchema: Yup.object(newSchema()),
    validateOnChange: false,
    onSubmit: (formaValue) =>{
      console.log('Forluario enviado');
      console.log(formValue);
    }
  })

  return (
    <Form className='add-edit-student-form' onSubmit={formik.handleSubmit} >
      <Form.Input name="name" placeholder="Nombre" value={formik.val} />
      <Form.Input name="paternal" placeholder="Apellido paterno" />
      <Form.Input name="maternal" placeholder="Apellido materno" />
      <Dropdown placeholder='Curso' fluid selection search options={gradesFormat} />
      <Form.Input name="nameApoderado" placeholder="Nombre Apoderado" />
      <Dropdown placeholder='Comuna' fluid selection search options={COMMUNE} /> 
      <Form.Input name="address_street" placeholder="Dirección" />
      <Form.Input name="email" type="email" placeholder="Email" />
      <Form.Input name="phone" placeholder="Teléfono" />
      <Button type='submit' primary fluid content="Enviar"></Button>
    </Form>
  )
}

function formatDropdownData(data){
  return map(data, (item) =>({
    key: item.id,
    text: item.level + item.letter,
    value: item.level + item.letter,
  }));
}

function initialValues(){
  return{
    name: "",
    paternal: "",
    maternal: "",
    nameApoderado: "",
    address_street: "",
    email: "",
    phone: "",
  }
}

function newSchema(){
  return{
    name: Yup.string().required(true),
    paternal: Yup.string().required(true),
    maternal: Yup.string().required(true),
    nameApoderado: Yup.string().required(true),
    address_street: Yup.string().required(true),
    email: Yup.string().email(true).required(true),
    phone: Yup.string().required(true),
  }
}
