import React, { useEffect, useState } from 'react';
import { HeaderPage } from '../../components/Admin';
import { Loader } from 'semantic-ui-react';
import { useStudent, useAttendance } from '../../hooks';
import { AddAttendanceForm } from  "../../components/Admin";
import { ModalBasic } from "../../components/Common";


export function AttendanceAdmin() {
    const [showModal, setShowModal] = useState(false);
    const [titleModal, setTitleModal] = useState(null);
    const [contentModal, setContentModal] = useState(null);
    const [refetch, setRefetch] = useState(false);

    const { loading, students } = useA();

    useEffect(() => console.log("Hola"), []);

    const openCloseModal = () => setShowModal((prev) => !prev);
    const onRefetch = () => setRefetch((prev) => !prev);
    
    const AddAttendance = () =>{
        setTitleModal("Ingreso de asistencia");
        setContentModal(<AddAttendanceForm onClose={openCloseModal} onRefetch={onRefetch} />);
        openCloseModal();
      };
    
  return (
    <>
        <HeaderPage title="Asistencia" btnTitle="Nueva lista" btnClick={AddAttendance}/>
        {loading ? (
            <Loader active inline="centered">
                Cargando...
            </Loader>
        ):(
            <h1>Lista de Asistencia</h1>
        )}
        <ModalBasic show={showModal} onClose={openCloseModal} title={titleModal} children={contentModal} />
    </>
  )
}
