import { useState } from "react";
import { getGradesApi, addGradeApi } from "../api/grade";
import { useAuth } from "./useAuth";

export function useGrade(){
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);
    const [grades, setGrades] = useState(null);
    const { auth } = useAuth();

    const getGrades = async () =>{
        try {
            setLoading(true);
            const response = await getGradesApi();
            setLoading(false);
            setGrades(response);
        } catch (error) {
            setError(error);
            setLoading(false);
        }
    };

    const addGrade = async (data) =>{
        try {
            setLoading(true);
            addGradeApi(data, auth.token);
            setLoading(false)
        } catch (error) {
            setError(error);
            setLoading(false);
        }
    }
    return{
        loading,
        error,
        grades,
        getGrades,
    }
}
