import React, { useEffect, useState } from 'react';
import { Form, Button, Dropdown } from 'semantic-ui-react';
import { useGrade } from "../../../../hooks"
import "./AddEditStudentForm.scss";
import { map } from "lodash";
import { useFormik } from 'formik';
import * as Yup from "yup";

import { COMMUNE } from "../../../../utils/constants"
export function AddEditStudentForm() {
  const [gradesFormat, setGradesFormat] = useState([]);

  const { getGrades, grades } = useGrade();
  
  useEffect(() => getGrades(), [])
  useEffect(() =>{
    setGradesFormat(formatDropdownData(grades));
  }, [grades]);
  
  const formik = useFormik({
    initialValues: initialValues(),
    validationSchema: Yup.object(newSchema()),
    validateOnChange: false,
    onSubmit: (formValue) =>{
      console.log('Formulario enviado');
      console.log(formValue);
    }
  })

  return (
    <Form className='add-edit-student-form' onSubmit={formik.handleSubmit} >
      <Form.Input 
        name="name" 
        placeholder="Nombre" 
        value={formik.values.name} 
        onChange={formik.handleChange} 
        error={formik.errors.name} 
      />
      <Form.Input 
        name="paternal" placeholder="Apellido paterno" value={formik.values.paternal} onChange={formik.handleChange} error={formik.errors.paternal} />
      <Form.Input name="maternal" placeholder="Apellido materno" value={formik.values.maternal} onChange={formik.handleChange} error={formik.errors.maternal}/>
      <Dropdown placeholder='Curso' fluid selection search options={gradesFormat} value={formik.values.grade} onChange={(_, data)=> formik.setFieldValue('grade', data.value)} error={formik.errors.grade} />
      <Form.Input name="nameApoderado" placeholder="Nombre Apoderado" value={formik.values.nameApoderado} onChange={formik.handleChange} error={formik.errors.nameApoderado} />
      <Dropdown placeholder='Comuna' fluid selection search options={COMMUNE} value={formik.values.commune} onChange={(_, data)=> formik.setFieldValue('commune', data.value)} error={formik.errors.commune} /> 
      <Form.Input name="address_street" placeholder="Dirección" value={formik.values.address_street} onChange={formik.handleChange} error={formik.errors.address_street}  />
      <Form.Input name="email" type="email" placeholder="Email" value={formik.values.email} onChange={formik.handleChange} error={formik.errors.email} />
      <Form.Input name="phone" placeholder="Teléfono" value={formik.values.phone} onChange={formik.handleChange} error={formik.errors.phone} />
      <Button type='submit' primary fluid content="Enviar"></Button>
    </Form>
  )
}

function formatDropdownData(data){
  return map(data, (item) =>({
    key: item.id,
    text: item.level + item.letter,
    value: item.level + item.letter,
  }));
}

function initialValues(){
  return{
    name: "",
    paternal: "",
    maternal: "",
    grade: "",
    nameApoderado: "",
    commune: "",
    address_street: "",
    email: "",
    phone: "",
  }
}

function newSchema(){
  return{
    name: Yup.string().required(true),
    paternal: Yup.string().required(true),
    maternal: Yup.string().required(true),
    grade: Yup.number().required(true),
    nameApoderado: Yup.string().required(true),
    commune: Yup.number().required(true),
    address_street: Yup.string().required(true),
    email: Yup.string().email(true).required(true),
    phone: Yup.string().required(true),
  }
}
