import React from 'react';
import "./SideMenu.scss";
import { Menu, Icon, Dropdown } from 'semantic-ui-react';
import { Link, useLocation } from "react-router-dom";


export function SideMenu(props) {
    const {children } = props;
    const { pathname } = useLocation();
  return (
    <div className='side-menu-admin'>
        <MenuLeft pathname={pathname} />
        <h1>Hola SideMenu</h1>
        <div className='content'>{children}</div>
    </div>
  )
}

function MenuLeft(props){
    const { pathname } = props;

    return(
        <Menu  borderless className='side' vertical>
            <Dropdown item text='Hola'>
                <Dropdown.Menu>
                    <Dropdown.Header>GY</Dropdown.Header>
                    <Dropdown.Item>huhu</Dropdown.Item>
                    <Dropdown.Item>huhu</Dropdown.Item>
                    <Dropdown.Item>huhu</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
            
            <Menu.Item as={Link} to={'/admin'} active={pathname === "/admin"}>
                <Icon name='student' /> Alumnos
            </Menu.Item>
            <Menu.Item as={Link} to={'/admin/payments'} active={pathname === "/admin/payments"}>
                <Icon name='money bill alternate outline' /> Pagos
            </Menu.Item>
            <Menu.Item as={Link} to={'/admin'} active={pathname === "/admin"}>
                <Icon name='list' /> Asistencia
            </Menu.Item>
            <Menu.Item as={Link} to={'/admin'} active={pathname === "/admin"}>
                <Icon name='bus' /> Mantenimiento Transporte
            </Menu.Item>
        </Menu>
    );
    
}
